<div class="white-background">    <div class="minimum-height">        <h2>Stock In</h2>        <div class="table-responsive">            <table id="StockInDataTable" class="table table-hover">                <?php $count = 1; ?>                <thead>                <tr>                    <th scope="col">#</th>                    <th scope="col">Location Name</th>                    <th scope="col"></th>                </tr>                </thead>                <tbody>                <?php foreach ($locations as $location) : ?>                    <tr>                        <td><?php echo $count; ?>.</td>                        <td><?php echo $location['location']; ?></td>                        <td>                            <div class="bs-component">                                <?php $attribute = array('onsubmit' => "return confirm('Are you sure, you want to gather all products from this location?')") ?>                                <?php echo form_open('Stock/transferitems/' . $location['locations_table_id'], $attribute); ?>                                <input type="submit" class="btn btn-primary btn-block" data-toggle="tooltip"                                       data-placement="bottom" title=""                                       data-original-title="Click to gather all products from this location"                                       value="Gather Products" <?php if ($location['locations_table_id'] == 1) : echo "disabled='disabled'"; endif; ?>>                                <?php echo form_close(); ?>                            </div>                        </td>                    </tr>                    <?php $count++; ?>                <?php endforeach; ?>                </tbody>            </table>        </div>    </div></div>